class Solution {
public:
using ll= long long;
using sg=string;
typedef pair<int, int> pll;
typedef vector<int> vll;
typedef vector<pll> vpll; 
typedef map<int,int> mll;
#define ff first
#define ss second
#define pb push_back
#define fl(i,n) for(int i=0;i<n;i++)
#define vr(v) v.begin(),v.end()
#define flp(i,a,b) for(int i = a; i <= b; i++)
    ll minimumCost(int m, int n, vector<int>& v1, vector<int>& v2) {
        ll a=1;
        ll b=1;
        vector<pair<ll,ll>>v;
        for(auto it:v1){
            v.push_back({it,1LL});
        }
        for(auto it:v2){
            v.push_back({it,0LL});
        }
        ll c=0;
        sort(vr(v));
        reverse(vr(v));
        for(auto it:v){
            if(it.ss==1){
                c+=(b*it.ff);
                a++;
            }else{
                c+=(a*it.ff);
                b++;
            }
        }
        return c;
    }
};
