class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int,int>> p;
        for(int i=0;i<m-1;i++){
            p.push_back({h[i],1});
        }
        for(int i=0;i<n-1;i++){
            p.push_back({v[i],0});
        }
        sort(p.begin(),p.end(),greater<pair<int,int>>());
        long long int ans=0,ch=1,cv=1;
        for(auto it:p){
            if(it.second==0){
                ans+=it.first*ch;
                cv++;
            }
            else{
                ans+=it.first*cv;
                ch++;
            }
        }
        return ans;
    }
};
