#define ll long long
class Solution {
public:
    long long minimumCost(int r, int c, vector<int>hc, vector<int>vc) {
        vc.insert(vc.begin(),0);
        hc.insert(hc.begin(),0);
        int h=1,v=1;
        sort(hc.begin(),hc.end());
        sort(vc.begin(),vc.end());
        int hp=hc.size()-1,vp=vc.size()-1;

        ll ans=0;
        while(hp>=0 && vp>=0) {
            if(hc[hp]>vc[vp]) {
                ans += 1LL*hc[hp]*h;
                v++;
                hp--;
            } else {
                ans += 1LL*vc[vp]*v;
                h++;
                vp--;
            }
        }
        return ans;
    }
};
