class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        h_m = 1
        v_m = 1
        score = 0
        h = []
        for cut in horizontalCut:
            heappush(h, (-1*cut, "horizontal"))
        for cut in verticalCut:
            heappush(h, (-1*cut, "vertical"))
        while h:
            (cut_val, direction) = heappop(h)
            if direction == "horizontal":
                score -= cut_val * h_m
                v_m += 1
            else:
                score -= cut_val * v_m
                h_m += 1
        return score
