class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
         sort(h.begin(), h.end(), greater<int>());
    sort(v.begin(), v.end(), greater<int>());

    int hi = 1, vi = 1;
    long long cost = 0;
    int i = 0, j = 0;


    while (i < h.size() && j < v.size()) {
        if (h[i] > v[j]) {
            cost += h[i] * vi;
            hi++;
            i++;
        } else {
            cost += v[j] * hi;
            vi++;
            j++;
        }
    }

    while (i < h.size()) {
        cost += h[i] * vi;
        hi++;
        i++;
    }


    while (j < v.size()) {
        cost += v[j] * hi;
        vi++;
        j++;
    }

    return cost;
    }
};
