func minimumCost(m int, n int, horizontalCut []int, verticalCut []int) int64 {
    sort.Ints(horizontalCut)
    sort.Ints(verticalCut)
    i,j:=m-2,n-2
    t1,t2:=int64(1),int64(1) //分别表示垂直方向和水平方向有几块
    t:=int64(0)
    for i>=0&&j>=0{
        if i>=0&&horizontalCut[i]>=verticalCut[j]{
            t+=(int64(horizontalCut[i])*t2)
            t1++
            i--
        }else{
            if j>=0{
                t+=(int64(verticalCut[j])*t1)
                t2++
                j--
            }
        }
    }
    if i>=0{
        for ;i>=0;i--{
            t+=(int64(horizontalCut[i])*t2)
        }
    }
    if j>=0{
        for ;j>=0;j--{
            t+=(int64(verticalCut[j])*t1)
        }
    }
    return t
}
