class Solution {
public:
long long f(int m,int n,vector<int>& horizontalCut, vector<int>& verticalCut)
{
     long long res = 0;
       long long hzntl = 1, vert = 1;

    long long i = 0, j = 0;
    while (i < m && j < n)
    {
        if (horizontalCut[i] > verticalCut[j])
        {
            res += horizontalCut[i] * vert;

            hzntl++;
            i++;
        }
        else
        {
            res += verticalCut[j] * hzntl;
            vert++;
            j++;
        }
    }
    long long total = 0;
    while (i < m)
        total += horizontalCut[i++];
    res += total * vert;

    total = 0;
    while (j < n)
        total += verticalCut[j++];
    res += total * hzntl;

    return res;
}
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
           sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());
    sort(verticalCut.begin(), verticalCut.end(), greater<int>());

    return f(m-1,n-1,horizontalCut,verticalCut);
    }
};
