#define vi vector<int>
#define ll long long
class Solution {
public:
    ll minimumCost(int m, int n, vi &hcut, vi &vcut) {
        priority_queue<int> hq(hcut.begin(), hcut.end());
        priority_queue<int> vq(vcut.begin(), vcut.end());

        ll ans = 0;
        ll h_pieces = 1, v_pieces = 1;

        while (!hq.empty() || !vq.empty()) {
            if (vq.empty() || (!hq.empty() && hq.top() > vq.top())) {
                ans += (ll)(hq.top() * v_pieces);
                h_pieces++;
                hq.pop();
            } else {
                ans += (ll)(vq.top() * h_pieces);
                v_pieces++;
                vq.pop();
            }
        }

        return ans;
    }
};
