class Solution {
public:
    using ll = long long;
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        ll ans = 0;

        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());

        ll j = 0, i = 0;

        ll v_ct = 0, h_ct = 0;

        while(j < m-1 && i < n-1){
            if(v[i] > h[j]){
                v_ct++;
                i++;
                ans += (h_ct + 1) * v[i - 1];
            }else if(v[i] < h[j]){
                h_ct++;
                ans += (v_ct + 1) * (h[j]);
                j++;

            }else{
                if(h_ct >= v_ct){
                    ans += (v_ct + 1) * h[j];
                    h_ct++;
                    j++;
                }else{
                    ans += (h_ct + 1)*v[i];
                    i++;
                    v_ct++;
                }
            }
        }

        while(i < n-1){
            ans += (h_ct + 1)*(v[i]);
            i++;
        }

        while(j < m-1){
            ans += (v_ct + 1)*h[j];
            j++;
        }

        return ans;

    }
};
