class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
          Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        long sumhor=0;
        long sumver=0;
        for (int i = 0; i < horizontalCut.length; i++) {
            sumhor+=horizontalCut[i];
        }
        for (int i = 0; i < verticalCut.length; i++) {
            sumver+=verticalCut[i];
        }
        int hori= horizontalCut.length-1;
        int veri = verticalCut.length-1;
        int hor=0;
        int ver=0;
        long sum=0;
        for (; hori>=0&&veri>=0; ) {
            if(horizontalCut[hori]>verticalCut[veri])
            {
                sum+=horizontalCut[hori]*(ver+1);
                sumhor-=horizontalCut[hori];
                hor++;
                hori--;
            }else if(horizontalCut[hori]<verticalCut[veri]){
                sum+=verticalCut[veri]*(hor+1);
                sumver-=verticalCut[veri];
                ver++;
                veri--;
            }else {
                if(sumhor>=sumver)
                {
                    sum+=horizontalCut[hori]*(ver+1);
                    sumhor-=horizontalCut[hori];
                    hor++;
                    hori--;

                }else {
                    sum+=verticalCut[veri]*(hor+1);
                    sumver-=verticalCut[veri];
                    ver++;
                    veri--;

                }
            }
        }
        while (hori>=0)
        {
            sum+=horizontalCut[hori]*(ver+1);
            hor++;
            hori--;
        }
        while (veri>=0)
        {
            sum+=verticalCut[veri]*(hor+1);
            ver++;
            veri--;
        }
        return sum;
    }
}
