func minimumCost(m int, n int, horizontalCut []int, verticalCut []int)(ans int64) {
    // 贪心，应该先切最大的
    sort.Ints(horizontalCut)
    sort.Ints(verticalCut)
    c,d:=1,1 //(横竖状态的块数)
    a,b := m-2,n-2
    for a>=0 || b>=0{
        if a>=0 &&b >= 0{
            if horizontalCut[a]>verticalCut[b]{
                ans+=int64(horizontalCut[a]*c)
                d++
                a--
            }else{
                ans+=int64(verticalCut[b]*d)
                c++
                b--
            }
        }else if a>=0{
            for a>=0{
                ans+=int64(horizontalCut[a]*c)
                d++
                a--
            }
        }else if b>=0{
            for b>=0{
                ans+=int64(verticalCut[b]*d)
                c++
                b--
            }

        }
    }
    return 
}
