class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        verticalCut.sort(reverse=True)
        horizontalCut.sort(reverse=True)
        lenver, lenhor = len(verticalCut), len(horizontalCut)

        h,v, horipiece, verpiece= 0,0, 1,1
        result = 0

        while v<lenver and h<lenhor:
            if verticalCut[v] < horizontalCut[h]:
                result += verpiece * horizontalCut[h]
                horipiece+=1
                h+=1
            else:
                result += horipiece * verticalCut[v]
                verpiece+=1
                v+=1

        while v< lenver:
            result+= verticalCut[v] * horipiece
            v+=1

        while h< lenhor:
            result+= verpiece * horizontalCut[h]
            h+=1
        return result
