class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
         vector<pair<long long, char>>v;
        for(auto it:horizontalCut)v.push_back({it,'h'});
        for(auto it:verticalCut)v.push_back({it,'v'});
        sort(v.begin(),v.end());
        long long ans=0;
        long long H=1,V=1;
        for(int i=v.size()-1;i>=0;i--){
            if(v[i].second=='h'){
                ans+=(v[i].first*H);
                V++;
            }
            else{
                ans+=(v[i].first*V);
                H++;
            }
        }   

        return ans;
    }
};
