class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& harr, vector<int>& varr) {

        sort(harr.begin(), harr.end(), greater<int>());
        sort(varr.begin(), varr.end(), greater<int>());

        int hi = 0;
        int vi = 0;
        int hc = 1;
        int vc = 1;
        long long cost = 0;

        while (hi < harr.size() && vi < varr.size()) {

            if (harr[hi] > varr[vi]) {
                cost += harr[hi] * vc;
                hc++;
                hi++;
            } else {
                cost += varr[vi] * hc;
                vc++;
                vi++;
            }
        }

        while (hi < harr.size()) {
            cost += harr[hi] * vc;
            hi++;
        }

        while (vi < varr.size()) {
            cost += varr[vi] * hc;
            vi++;
        }

        return cost;
    }
};
