class Solution {
public:
    #define ll long long
    long long minimumCost(int m, int n, vector<int>& r, vector<int>& c) {
        if(m==1 && n==1) return 0;

        sort(r.begin(),r.end());
        sort(c.begin(),c.end());
        reverse(r.begin(),r.end());
        reverse(c.begin(),c.end());

        int i=0,j=0;
        ll h=1,v=1;
        ll ans = 0;
        // cout<<m<<" "<<n<<"\n";
        while(i<r.size() && j<c.size()){
            // cout<<i<<" "<<j<<"\n";
            if(r[i]>=c[j]){
                ans+= (r[i] * v);
                h++;
                i++;
            }
            else{
                ans+= (c[j]* h);
                v++;
                j++;
            }
        }

        while(i<r.size()){
            ans+= (r[i] * v);
                h++;
                i++;
        }
        while(j<c.size()){
             ans+= (c[j]* h);
                v++;
                j++;
        }


        return ans;
    }
};
