class Solution {
public:
    long long minimumCost(int m, int n, vector<signed>& h, vector<signed>& v) {
        int c1=1,c2=1;
        sort(h.begin(),h.end(),greater<int>());
        sort(v.begin(),v.end(),greater<int>());
        int l1=0,l2=0;
        int res=0;
        while(l1<n-1&&l2<m-1) {
            if(v[l1]>h[l2]) {
                res+=v[l1]*c2;
                c1++;
                l1++;
            }else{
                res+=h[l2]*c1;
                c2++;
                l2++;
            }
        }
        while(l1<n-1) {
            res+=v[l1]*c2;
            l1++;
        }
        while(l2<m-1) {
            res+=h[l2]*c1;
            l2++;
        }
        return res;
    }
};

