#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        sort(hor.begin(), hor.end());
        sort(ver.begin(), ver.end());
        ll ret = 0;
        int i = m-2;
        int j = n-2;
        while(i>=0 && j>=0){
            if(hor[i]>=ver[j]){
                ret+=1ll*hor[i]*((n-1)-j);
                i--;
            }
            else{
                ret+=1ll*ver[j]*((m-1)-i);
                j--;
            }
        }
        while(i>=0){
            ret+=1ll*hor[i]*(n);
            i--;
        }
        while(j>=0){
            ret+=1ll*ver[j]*(m);
            j--;
        }
        return ret;
    }
};
