class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
         int row = 1, col = 1;
         long long cost = 0;
        sort(h.begin(), h.end());
        sort(v.begin(), v.end());
        int j = m - 2, k = n - 2;

        while (j >= 0 && k >= 0) {
            if (h[j] > v[k]) {
                row++;
                cost += (col * h[j]);
                j--;
            } else {
                col++;
                cost += (row * v[k]);
                k--;
            }
        }

        while (j >= 0) {
            cost += (col * h[j]);
            j--;
        }

        while (k >= 0) {
            cost += (row * v[k]);
            k--;
        }

        return cost;
    }
};
