class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.rbegin(),horizontalCut.rend());
        sort(verticalCut.rbegin(),verticalCut.rend());

        long long ans = 0;

        long long int ptrH = 0,ptrV = 0,countH = 0,countV = 0;

        while(ptrH < m-1 && ptrV < n-1)
        {
            if(horizontalCut[ptrH] >= verticalCut[ptrV])
            {
                ans += (countV+1) * horizontalCut[ptrH];
                countH++;
                ptrH++;
            }
            else
            {
                ans += (countH+1) * verticalCut[ptrV];
                countV++;
                ptrV++;
            }
        }

        while(ptrV < n-1)
        {
                ans += (countH+1) * verticalCut[ptrV];
                countV++;
                ptrV++;
        }

        while(ptrH < m-1)
        {
                ans += (countV+1) * horizontalCut[ptrH];
                countH++;
                ptrH++;
        }
        return ans;
    }
};
