class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        int row = 0;
        int col = 0,i = m-2, j = n-2;
        sort(h.begin(), h.end());
        sort(v.begin(), v.end());
        long long ans = 0;
        for(int cnt = 0;i>=0 || j>=0;){
            if(i>=0 && j>=0){
                if(h[i]>v[j]){
                    ans += (col+1)*h[i--];
                    row++;
                }else {
                    ans+=(row+1)*v[j--];
                    col++;
                }
            }else {
                if(i>=0){
                    ans += (col+1)*h[i--];
                    row++;
                }else {
                    ans+=(row+1)*v[j--];
                    col++;
                }
            }
        }
        return ans;
    }
};
