class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(v.rbegin(), v.rend());
        sort(h.rbegin(), h.rend());
        long long i = 0, j = 0, hn = h.size(), vn = v.size();
        long long hm = 1, vm = 1;
        long long cost = 0;
        while(i < hn && j < vn) {
            if(h[i] > v[j]) {
                cost += h[i] * hm;
                vm++;
                i++;
            } else {
                cost += v[j] * vm;
                hm++;
                j++;
            }
        }
        while(i < hn) {
            cost += h[i] * hm;
            i++;
            vm++;
        }
        while(j < vn) {
            cost += v[j] * vm;
            j++;
            hm++;
        }
        return cost;
    }
};
