class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = [(cost, 1) for cost in horizontalCut] + [(cost, 2) for cost in verticalCut]
        cuts.sort(key=lambda x: -x[0])

        h = 1
        v = 1
        res = 0

        for cost, cut_type in cuts:
            if cut_type == 1:
                res += cost * v
                h += 1
            else:
                res += cost * h
                v += 1
        return res
