class Solution {
public:
long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
    sort(begin(hc), end(hc), greater<int>());
    sort(begin(vc), end(vc), greater<int>());
    long long i = 0, j = 0;
    long long res = 0;
    while (i + 1 < m && j + 1 < n) {
        if (hc[i] * (j + 1) + vc[j] * (i + 2) < hc[i] * (j + 2) + vc[j] * (i + 1)) {
            res += hc[i] * (j + 1);
            ++i;
        }
        else {
            res += vc[j] * (i + 1);
            ++j;
        }
    }
    for (; i + 1 < m; ++i)
        res += hc[i] * n;
    for (; j + 1 < n; ++j)
        res += vc[j] * m;
    return res;
}    
};
