class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        costs = []
        for c in horizontalCut:
            costs.append((c, "h"))
        for c in verticalCut:
            costs.append((c, "v"))

        costs.sort(key=lambda x: -x[0])

        horizontal_cuts = 1
        vertical_cuts = 1

        res = 0
        for c,type_ in costs:
            if type_=="h":
                res += c*vertical_cuts
                horizontal_cuts+=1
            elif type_=="v":
                res += c*horizontal_cuts
                vertical_cuts+=1

        return res
