class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        pieces = [1,1]
        cost = 0
        cut = sorted([[i,0] for i in horizontalCut]+[[i,1] for i in verticalCut], key = lambda x:x[0])
        for i in range(m+n-3,-1,-1):
            cost += cut[i][0] * pieces[1-cut[i][1]]
            pieces[cut[i][1]]+=1
        return cost
