class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
         multiset<pair<int,char>>s;
        for(int i=0;i<m-1;i++){
            s.insert({h[i],'h'});
        }
        for(int i=0;i<n-1;i++){
            s.insert({v[i],'v'});
        }
        long long hcnt=0,vcnt=0,ans=0;
        while(s.size()>0){
            pair<int,char>p=*prev(s.end());
            s.erase(prev(s.end()));
            if(p.second=='h'){
                ans+=(vcnt+1)*p.first;
                hcnt++;
            }
            else{
                ans+=(hcnt+1)*p.first;
                vcnt++;
            }
        }
    return ans;
    }
};
