class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& nums1, vector<int>& nums2) {
        int row_cut = 1;
        int col_cut=1;
        long long ans=0;
        sort(nums1.rbegin(),nums1.rend());
        sort(nums2.rbegin(),nums2.rend());
        int i = 0;
        int j=0;
        while(i<nums1.size() || j<nums2.size()){
            int val1 = i<nums1.size()?nums1[i]:0;
            int val2 = j<nums2.size()?nums2[j]:0;
            if(val1>val2){
                //horizontal cut
                ans+=(long long)(col_cut)*val1;
                row_cut++;
                i++;
            }
            else{
                //vertical cut
                ans+=(long long)(row_cut)*val2;
                col_cut++;
                j++;
            }
        }
        return ans;
    }
};
