class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hcut, vector<int>& vcut) {
        long long int sum=0;
        if(n==1 && m==1)
                return 0;
        sort(hcut.rbegin(),hcut.rend());
        sort(vcut.rbegin(),vcut.rend());
        long long int i=0,j=0,v=0,h=0;
        while(i<m-1 && j<n-1)
        {
            if(hcut[i]>vcut[j])
            {
                sum+=((long long)hcut[i]*(long long)(v+1));
                h++;
                i++;
            }
            else
            {
                sum+=((long long)vcut[j]*(long long)(h+1));
                v++;
                j++;
            }
        }
        while(i<m-1)
        {
            sum+=((long long)hcut[i]*(long long)(v+1));
            h++;
            i++;
        }
        while(j<n-1)
        {
            sum+=((long long)vcut[j]*(long long)(h+1));
            v++;
            j++;
        }
        return sum;
    }
};
