class Solution:
    def minimumCost(self, m: int, n: int, hc: List[int], vc: List[int]) -> int:
        hc.sort(reverse=True)
        vc.sort(reverse=True)

        h, v = 0, 0
        hp, vp = 1, 1
        ans = 0

        while h < len(hc) and v < len(vc):
            if hc[h] > vc[v]:
                ans += hc[h] * vp
                hp += 1
                h += 1
            else:
                ans += vc[v] * hp
                vp += 1
                v += 1

        while h < len(hc):
            ans += hc[h] * vp
            h += 1

        while v < len(vc):
            ans += vc[v] * hp
            v += 1

        return ans
