#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& grid, vector<int>& nums) {
        sort(grid.begin(), grid.end(), greater<int>());
        sort(nums.begin(), nums.end(), greater<int>());

        long long c = 1, d = 1;
        long long res = 0;
        int i=0;
        int j=0;
        while (i < grid.size() && j < nums.size()) {
            if (grid[i] >= nums[j]) {
                res += static_cast<long long>(grid[i]) * d;
                c++;
                i++;
            } else {
                res += static_cast<long long>(nums[j]) * c;
                d++;
                j++;
            }
        }

        while (i < grid.size()) {
            res += static_cast<long long>(grid[i]) * d;
            c++;
            i++;
        }

        while (j < nums.size()) {
            res += static_cast<long long>(nums[j]) * c;
            d++;
            j++;
        }

        return res;
    }
};
