class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& c) {
               vector<pair<int,bool>>v;
        for(int i=0;i<h.size();i++){
               v.push_back({h[i],1});
        }

        for(int i=0;i<c.size();i++){
               v.push_back({c[i],0});
        }


        sort(v.begin(),v.end(),greater<pair<int,bool>>());
        long long int cost=0;
      int ho=1;
      int ver=1;
        for(int i=0;i<v.size();i++){
            bool fl=v[i].second;

            if(fl){
                cost = cost + (ver * (v[i].first));
                ho++;
            }else{
                 cost = cost + (ho * (v[i].first));
                ver++;
            }
        }
        return cost;
    }
};
