class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
         long long vpc=1,hpc=1;
        vector<pair<long long,bool>>v;
        for(auto i : horizontalCut)
        {
            v.push_back({i,0});
        }
        for(auto i : verticalCut)
        {
            v.push_back({i,1});
        }
        sort(v.rbegin(),v.rend());
        long long ans=0;
        for(auto i : v)
        {
            if(i.second==0)
            {
                vpc++;
                ans+=hpc*i.first;
            }
            else
            {
                hpc++;
                ans+=vpc*i.first;
            }
        }
        return ans;
    }
};
