class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long fc=0;
        sort(horizontalCut.begin(),horizontalCut.end(),greater<int>());
        sort(verticalCut.begin(),verticalCut.end(),greater<int>());
        long long hs,vs,hi,vi;
        hs=1;
        vs=1;
        hi=0;
        vi=0;
        long long temp1=m-1;
        long long temp2=n-1;
        while(hi<temp1 || vi<temp2){
            if(hi<temp1 && (vi>=temp2 || horizontalCut[hi]>=verticalCut[vi])){
                fc+=horizontalCut[hi]*vs;
                hi++;
                hs++;
            }
            else if(vi<temp2){
                fc+=verticalCut[vi]*hs;
                vs++;
                vi++;
            }

        }
        return fc;
    }
};
