class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        v = 1
        h = 1
        ans = 0

        horizontalCut.sort(reverse = True)
        h_d = deque(horizontalCut)

        verticalCut.sort(reverse = True)
        v_d = deque(verticalCut)

        while h_d and v_d:
            if h_d[0] > v_d[0]:
                c = h_d.popleft()
                ans += c * v
                h += 1
            else:
                c = v_d.popleft()
                ans += c * h
                v += 1

        while h_d:
            ans += h_d.popleft() * v

        while v_d:
            ans += v_d.popleft() * h

        return ans
