class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>>all;
        for(auto&x:horizontalCut){
            all.push_back({x,0});
        }
        for(auto&x:verticalCut){
            all.push_back({x,1});
        }

        sort(all.begin(),all.end());
        reverse(all.begin(),all.end());
        long long ans = 0;
        long long v = 1,h = 1;
        for(auto&x:all){
            long long cur = x.first;
            long long val = 0;
            if(x.second == 1){
                cur *= h;
            }
            else{
                cur *= v;
            }
            ans += cur;
            if(x.second == 1)v++;
            else h++;
        }

        return ans;
    }
};
