#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    typedef long long ll;
    ll solve(vector<int>& h, vector<int>& v) {
        int i = 0, j = 0;
        int hPieces = 1, vPieces = 1;
        ll ans = 0;
        while (i < h.size() && j < v.size()) {
            if (h[i] > v[j]) {
                ll cost = h[i];
                ll pieces = vPieces;
                ans += cost * pieces;
                hPieces++;
                i++;
            } else {
                ll cost = v[j];
                ll pieces = hPieces;
                ans += cost * pieces;
                vPieces++;
                j++;
            }
        }
        while (i < h.size()) {
            ll cost = h[i];
            ll pieces = vPieces;
            ans += cost * pieces;
            i++;
        }
        while (j < v.size()) {
            ll cost = v[j];
            ll pieces = hPieces;
            ans += cost * pieces;
            j++;
        }
        return ans;
    }

    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end());
        reverse(h.begin(), h.end());
        sort(v.begin(), v.end());
        reverse(v.begin(), v.end());
        ll ans = solve(h, v);
        return ans;
    }
};
