class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hh, vector<int>& vv) {
        long long h=0,v=0,hp=1,vp=1,ans=0;
        sort(hh.rbegin(),hh.rend());
        sort(vv.rbegin(),vv.rend());
        swap(n,m);


        while(v<m-1&&h<n-1){
            if(hh[h]<vv[v]){
                ans+=hp*vv[v];
                vp++;
                v++;
            }
            else{
                ans+=vp*hh[h];
                hp++;
                h++;

            }
        }

        while(v<m-1){

                ans+=hp*vv[v];

                v++;
            }
              while(h<n-1){

                ans+=vp*hh[h];

                h++;
            }
            return ans;

    }
};
