class Pair{
    int val;
    boolean second;
    Pair(int val,boolean second)
    {
        this.val=val;
        this.second=second;
    }
}

class Solution {
    public long minimumCost(int m, int n, int[] hc, int[] vc) {
        ArrayList<Pair> arr=new ArrayList<>();
        for(int i=0;i<hc.length;i++)
        {
            arr.add(new Pair(hc[i],true));
        }
        for(int j=0;j<vc.length;j++)
        {
            arr.add(new Pair(vc[j],false));
        }
        Collections.sort(arr,(a,b)->b.val-a.val);
        int h=1;
        int v=1;
        long ans=0;
        for(int i=0;i<arr.size();i++)
        {
            int val=arr.get(i).val;
            boolean type=arr.get(i).second;
            if(type)
            {
                ans+=(val*v);
                h++;
            }
            else
            {
                ans+=(val*h);
                v++;
            }
        }
        return ans;
    }
}
