class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans = 0 ;
        int a = m-2;
        int b = n-2;
        int c = 1;
        int d = 1;
        sort(h.begin(),h.end());
       sort(v.begin(),v.end());
        while(a>=0 && b>=0)
        {
           if(h[a]>v[b])
           {
            c++;
            ans+=d*h[a];
            a--;
           }
           else
           {
            d++;
            ans+=c*v[b];
            b--;
           }
        }
        while(a>=0)
        {
            ans+=d*h[a];
            a--;
        }
        while(b>=0)
        {
            ans+=v[b]*c;
            b--;
        }
        return ans;
    }
};
