class Solution {
public:
    long long minimumCost(int m, int n, vector<int> &hCut, vector<int> &vCut)
{

    int h = 0, v = 0;
    int hP = 1, vP = 1;
    long long ans = 0;
    sort(hCut.begin(), hCut.end(), greater<int>());
    sort(vCut.begin(), vCut.end(), greater<int>());

    while (h < m - 1 && v < n - 1)
    {
        if (hCut[h] >= vCut[v])
        {
            ans += hCut[h] * vP * 1LL;
            h++;
            hP++;
        }
        else
        {
            ans += vCut[v] * hP * 1LL;
            v++;
            vP++;
        }
    }

    while (h < m-1)
    {
        ans += hCut[h] * vP * 1LL;
        h++;
        hP++;
    }

    while (v < n-1)
    {
        ans += vCut[v] * hP * 1LL;
        v++;
        vP++;
    }

    return ans;
}
};
