#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        // Sort the cuts in descending order
        sort(hc.rbegin(), hc.rend());
        sort(vc.rbegin(), vc.rend());

        int h = 0, v = 0;
        int hp = 1, vp = 1;
        long long totalCost = 0;

        while (h < hc.size() && v < vc.size()) {
            if (hc[h] > vc[v]) {
                totalCost += hc[h] * vp;
                hp++;
                h++;
            } else {
                totalCost += vc[v] * hp;
                vp++;
                v++;
            }
        }

        // Add remaining horizontal cuts
        while (h < hc.size()) {
            totalCost += hc[h] * vp;
            h++;
        }

        // Add remaining vertical cuts
        while (v < vc.size()) {
            totalCost += vc[v] * hp;
            v++;
        }

        return totalCost;
    }
};
