class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& hor, vector<int>& ver) {
        sort(hor.begin(), hor.end(), greater<int>());
        sort(ver.begin(), ver.end(), greater<int>());
        long long ans = 0;
        int h = 1, v = 1; 
        int i = 0, j = 0; 
        while (i < hor.size() && j < ver.size()) {
            if (hor[i] > ver[j]) {
                ans += (long long)hor[i] * v;
                h++;
                i++;
            } else {
                ans += (long long)ver[j] * h;
                v++;
                j++;
            }
        }
        while (i < hor.size()) {
            ans += (long long)hor[i]*v;
            i++;
        }
        while (j < ver.size()) {
            ans += (long long)ver[j]*h;
            j++;
        }
        return ans;
    }
};
