class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hori, vector<int>& verti) {
        long long ver=1,hor=1;
        long long ans=0;
        sort(hori.begin(),hori.end());
        sort(verti.begin(),verti.end());
        int i=m-2,j=n-2;
        while(i>=0 || j>=0)
        {
            if(i<0)
            {
                ans+=(verti[j]*ver);
                hor++;
                j--;
            }
            else if(j<0)
            {
                ans+=hori[i]*hor;
                ver++;
                i--;
            }
            else if(verti[j]>hori[i])
            {
                ans+=(verti[j]*ver);
                hor++;
                j--;
            }
            else
            {
                ans+=hori[i]*hor;
                ver++;
                i--;
            }
        }
        return ans;
    }
};
