#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hr, vector<int>& vr){
        sort(hr.begin(),hr.end(),greater<int>());
        sort(vr.begin(),vr.end(),greater<int>());

        int i=0,j=0;
        int hc=0,vc=0;
        ll ans=0;
        while(i<m-1 && j<n-1){
            if(hr[i] >= vr[j]){
                ll cost=1ll*(vc+1)*hr[i++];
                ans+=cost;
                hc++;
            }
            else{
                ll cost =1ll*(hc+1)*vr[j++];
                ans+=cost;
                vc++;
            }
        }
        while(i<m-1){
            ll cost=1ll*(vc+1)*hr[i++];
            ans+=cost;
            hc++;
        }
        while(j<n-1){
            ll cost=1ll*(hc+1)*vr[j++];
            ans+=cost;
            vc++;
        }
        return ans;
    }
};
