class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(),h.rend());
        sort(v.rbegin(),v.rend());

        long long x = 0, y = 0;
        long long ans = 0;
        long long hp = 1, vp = 1;
        while(x < h.size() && y < v.size()){
            if(h[x] > v[y]){
                ans += h[x]*vp;
                x++;
                hp++;
            }
            else{
                ans += v[y]*hp;
                y++;
                vp++;
            }
        }
        while(y < v.size()){
            ans += v[y]*hp;
            y++;
        }
        while(x < h.size()){
            ans += h[x]*vp;
            x++;
        }
        return ans;
    }
};
