class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& ver) {
        vector<pair<int, char>> v;
        for(int i = 0; i < h.size(); i++){
            v.push_back({h[i], 'h'});
        }
        for(int j = 0; j < ver.size(); j++){
            v.push_back({ver[j], 'v'});
        }
        sort(v.begin(), v.end(), greater<pair<int, char>>());
        long long hcuts = 0;
        long long vcuts = 0;
        long long total = 0;
        for(auto pair : v){
            if(pair.second == 'h'){
                total += (long long)(vcuts + 1) * pair.first;
                hcuts += 1;
            }
            else if(pair.second == 'v'){
                total += (long long)(hcuts + 1) * pair.first;
                vcuts += 1;
            }
        }
        return total;
    }
};
