class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        long long vFactor = 1;
        long long hFactor=1;
        sort(hCut.begin(),hCut.end());
        sort(vCut.begin(),vCut.end());
        int i = m-2;
        int j = n-2;
        long long cost = 0;
        while(i>=0 and j>=0)
        {
            if(hCut[i]>=vCut[j]){
                cost += hFactor*hCut[i];
                i--;
                vFactor++;
            }else{
                cost+= vFactor*vCut[j];
                j--;
                hFactor++;
            }
        }
        while(j>=0)
            cost+= vFactor*vCut[j--];
        while(i>=0)
            cost+= hFactor*hCut[i--];
        return cost;
    }
};
