using PII = pair<int, int>;
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long h{1};
        long long v{1};
        vector<PII>d;
        for(int i : horizontalCut) d.push_back({i, 0});
        for(int i : verticalCut) d.push_back({i, 1});
        sort(d.begin(), d.end(), greater<PII>());
        long long anw{0};
        for(auto i : d){
            if(i.second == 0){
                v++;
                anw += h * i.first;
            }else{
                h++;
                anw += v * i.first;
            }
        }
        return anw;
    }
};
