class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        f = list()
        for val in horizontalCut:
            f.append([val, 0])
        for val in verticalCut:
            f.append([val, 1])
        h, v = 0, 0
        f.sort(key=lambda x: x[0])
        n = len(f)
        ans = 0
        for i in range(n - 1, -1, -1):
            x = f[i]
            if x[1] == 0:
                ans += x[0] * (v + 1)
                h += 1
            else:
                ans += x[0] * (h + 1)
                v += 1
        return ans
