class Solution:
    def minimumCost(self, m: int, n: int, hc: List[int], vc: List[int]) -> int:
        h = 1
        v = 1
        que = []
        for i in hc:
            que.append((-1*i, 1))

        for i in vc:
            que.append((-1*i, 2))

        que.sort()
        res = 0
        while que:
            val, i = heapq.heappop(que)
            if i == 1:
                res -= val*h
                v += 1
            else:
                res -= val*v
                h += 1
            # print(val, i, v, h)
        return res
